import React, { useState } from "react";
const Digital_Clock=() =>
{
    const time = new Date().toLocaleDateString();
    const [current_time, setTime]=useState(time);
    const Update_Time =()=>{
        time = new Date().toLocaleDateString();
        setTime(time);
    }
    setInterval(Update_Time,1000);
    return(
      <h1>{time}</h1>  
    );
}
export default Digital_Clock;
